/* Author: Krishankant Ray * CodeVita 2017 Round 1 */ #include&lt;iostream&gt; #include&lt;vector&gt; using namespace std; int main() { int d,k=0; string arr; cin&gt;&gt;d; cin&gt;&gt;arr; int n=arr.length(); char R[n]; for(int f=0; f&lt;n; ) { R[f]=arr[k++]; f+=((2*d)-2); } for(int i=1; i&lt;d-1;i++) { R[i]=arr[k++]; for(int j=i; j&lt;n;) { j+=((2*d)-2-(2*i)); R[j]=arr[k++]; j+=((2*i)); if(j&lt;n) R[j]=arr[k++]; } } for(int l=d-1; l&lt;n;l+=((2*d)-2)) { R[l]=arr[k++]; } for(int f=0; f&lt;n;f++) { if(R[f]=='X') break; else cout&lt;&lt;R[f]; } return 0; }

